DROP TABLE IF EXISTS `SYSTEM_CLICK_COLLECT`;
CREATE TABLE `SYSTEM_CLICK_COLLECT`  (
  `ID` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主键',
  `NOTICE_CLICK_NUM` bigint(20) NOT NULL DEFAULT 0 COMMENT '公告每天点击量',
  `VIDEO_CLICK_NUM` bigint(20) NOT NULL DEFAULT 0 COMMENT '视频每天点击量',
  `ADVERT_CLICK_NUM` bigint(20) NOT NULL DEFAULT 0 COMMENT '广告每天点击量',
  `CREATETIME` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `REMARK` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `CHECK_STATUS` int(3) NOT NULL DEFAULT 0 COMMENT '状态 0正常，1禁用，2删除',
  `LAST_CHECK_TIME` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '最后修改时间',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

